import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { Observable, combineLatest, map, of, switchMap } from 'rxjs';
import { Account, Currency } from '../../models/account.model';
import { AccountService } from '../../services/account.service';
import { TransactionService } from '../../services/transaction.service';
import { AnalyticsService } from '../../services/analytics.service';

@Component({
  selector: 'app-transfer',
  templateUrl: './transfer.component.html',
  styleUrls: ['./transfer.component.css']
})
export class TransferComponent implements OnInit {
  transferForm!: FormGroup;
  accounts$!: Observable<Account[]>;
  sourceAccount: Account | null = null;
  targetAccount: Account | null = null;
  exchangeRate: number = 1;
  showConfirmation = false;

  constructor(
    private fb: FormBuilder,
    private accountService: AccountService,
    private transactionService: TransactionService,
    private analyticsService: AnalyticsService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.accounts$ = this.accountService.getAccounts();

    this.initForm();

    // Watch for source and target account changes to update exchange rate
    this.transferForm.get('sourceAccountId')?.valueChanges.subscribe(sourceId => {
      if (sourceId) {
        this.accountService.getAccountById(sourceId).subscribe(account => {
          this.sourceAccount = account || null;
          this.updateExchangeRate();
        });
      }
    });

    this.transferForm.get('targetAccountId')?.valueChanges.subscribe(targetId => {
      if (targetId) {
        this.accountService.getAccountById(targetId).subscribe(account => {
          this.targetAccount = account || null;
          this.updateExchangeRate();
        });
      }
    });
  }

  private initForm(): void {
    this.transferForm = this.fb.group({
      sourceAccountId: ['', Validators.required],
      targetAccountId: ['', Validators.required],
      amount: [0, [Validators.required, Validators.min(0.01)]],
      description: ['Transfert entre comptes']
    }, { validators: this.differentAccountsValidator });
  }

  // Validator to ensure source and target accounts are different
  private differentAccountsValidator(group: FormGroup): {[key: string]: any} | null {
    const sourceId = group.get('sourceAccountId')?.value;
    const targetId = group.get('targetAccountId')?.value;

    return sourceId && targetId && sourceId === targetId 
      ? { sameAccounts: true } 
      : null;
  }

  private updateExchangeRate(): void {
    if (this.sourceAccount && this.targetAccount) {
      if (this.sourceAccount.currency === this.targetAccount.currency) {
        this.exchangeRate = 1;
      } else if (this.sourceAccount.currency === 'USD' && this.targetAccount.currency === 'EUR') {
        // Get USD to EUR rate from analytics service
        this.analyticsService.getExchangeRates().subscribe((rates: { EUR_TO_USD: number, USD_TO_EUR: number }) => {
          this.exchangeRate = rates.USD_TO_EUR;
        });
      } else {
        // Get EUR to USD rate from analytics service
        this.analyticsService.getExchangeRates().subscribe((rates: { EUR_TO_USD: number, USD_TO_EUR: number }) => {
          this.exchangeRate = rates.EUR_TO_USD;
        });
      }
    }
  }

  getTargetAmount(): number {
    const amount = this.transferForm.get('amount')?.value || 0;
    return amount * this.exchangeRate;
  }

  onSubmit(): void {
    if (this.transferForm.invalid) return;

    this.showConfirmation = true;
  }

  confirmTransfer(): void {
    if (!this.sourceAccount || !this.targetAccount) return;

    const { sourceAccountId, targetAccountId, amount, description } = this.transferForm.value;
    const targetAmount = this.getTargetAmount();

    // Create withdrawal transaction from source account
    const withdrawalTransaction = {
      accountId: sourceAccountId,
      type: 'transfer' as const,
      amount: amount,
      currency: this.sourceAccount.currency,
      date: new Date(),
      description: description,
      targetAccountId: targetAccountId,
      exchangeRate: this.exchangeRate
    };

    // Process the transfer
    this.transactionService.addTransaction(withdrawalTransaction).pipe(
      switchMap(() => {
        // Update source account balance
        const newSourceBalance = this.sourceAccount!.currentBalance - amount;
        return this.accountService.updateAccount(sourceAccountId, { 
          currentBalance: newSourceBalance,
          totalWithdrawals: this.sourceAccount!.totalWithdrawals + amount
        });
      }),
      switchMap(() => {
        // Update target account balance
        const newTargetBalance = this.targetAccount!.currentBalance + targetAmount;
        return this.accountService.updateAccount(targetAccountId, { 
          currentBalance: newTargetBalance,
          totalDeposits: this.targetAccount!.totalDeposits + targetAmount
        });
      })
    ).subscribe(() => {
      // Navigate to transactions page after successful transfer
      this.router.navigate(['/transactions']);
    });
  }

  cancelTransfer(): void {
    this.showConfirmation = false;
  }

  cancel(): void {
    this.router.navigate(['/accounts']);
  }
}
<div class="transfer-container">
  <div class="header">
    <h2>Transfert entre Comptes</h2>
    <button class="btn btn-secondary" (click)="cancel()">Annuler</button>
  </div>

  <div class="form-container" *ngIf="!showConfirmation">
    <form [formGroup]="transferForm" (ngSubmit)="onSubmit()">
      <div class="form-group">
        <label for="sourceAccountId">Compte source *</label>
        <select 
          id="sourceAccountId" 
          formControlName="sourceAccountId"
          class="form-control"
          [ngClass]="{'invalid': transferForm.get('sourceAccountId')?.invalid && transferForm.get('sourceAccountId')?.touched}"
        >
          <option value="">Sélectionnez un compte</option>
          <option *ngFor="let account of (accounts$ | async)" [value]="account.id">
            {{ account.name }} ({{ account.currentBalance | currency:account.currency }})
          </option>
        </select>
        <div class="error-message" *ngIf="transferForm.get('sourceAccountId')?.invalid && transferForm.get('sourceAccountId')?.touched">
          Veuillez sélectionner un compte source.
        </div>
      </div>

      <div class="form-group">
        <label for="targetAccountId">Compte destination *</label>
        <select 
          id="targetAccountId" 
          formControlName="targetAccountId"
          class="form-control"
          [ngClass]="{'invalid': transferForm.get('targetAccountId')?.invalid && transferForm.get('targetAccountId')?.touched}"
        >
          <option value="">Sélectionnez un compte</option>
          <option *ngFor="let account of (accounts$ | async)" [value]="account.id">
            {{ account.name }} ({{ account.currentBalance | currency:account.currency }})
          </option>
        </select>
        <div class="error-message" *ngIf="transferForm.get('targetAccountId')?.invalid && transferForm.get('targetAccountId')?.touched">
          Veuillez sélectionner un compte destination.
        </div>
        <div class="error-message" *ngIf="transferForm.hasError('sameAccounts') && transferForm.get('targetAccountId')?.touched">
          Les comptes source et destination doivent être différents.
        </div>
      </div>

      <div class="form-group">
        <label for="amount">Montant à transférer *</label>
        <div class="amount-input-container" *ngIf="sourceAccount">
          <input 
            type="number" 
            id="amount" 
            formControlName="amount"
            class="form-control"
            [ngClass]="{'invalid': transferForm.get('amount')?.invalid && transferForm.get('amount')?.touched}"
          >
          <span class="currency-label">{{ sourceAccount?.currency }}</span>
        </div>
        <div class="error-message" *ngIf="transferForm.get('amount')?.invalid && transferForm.get('amount')?.touched">
          <span *ngIf="transferForm.get('amount')?.errors?.['required']">Le montant est requis.</span>
          <span *ngIf="transferForm.get('amount')?.errors?.['min']">Le montant doit être supérieur à 0.</span>
        </div>
      </div>

      <div class="exchange-info" *ngIf="sourceAccount && targetAccount && sourceAccount.currency !== targetAccount.currency">
        <div class="exchange-rate-info">
          <span class="label">Taux de change:</span>
          <span class="value">1 {{ sourceAccount.currency }} = {{ exchangeRate | number:'1.4-4' }} {{ targetAccount.currency }}</span>
        </div>
        <div class="converted-amount">
          <span class="label">Montant converti:</span>
          <span class="value">{{ getTargetAmount() | currency:targetAccount.currency }}</span>
        </div>
      </div>

      <div class="form-group">
        <label for="description">Description</label>
        <textarea 
          id="description" 
          formControlName="description"
          class="form-control"
          rows="3"
        ></textarea>
      </div>

      <div class="form-actions">
        <button type="button" class="btn btn-secondary" (click)="cancel()">Annuler</button>
        <button type="submit" class="btn btn-primary" [disabled]="transferForm.invalid">Continuer</button>
      </div>
    </form>
  </div>

  <div class="confirmation-container" *ngIf="showConfirmation">
    <div class="card">
      <div class="card-header">Confirmation du transfert</div>
      <div class="card-body">
        <div class="confirmation-details">
          <div class="detail-row">
            <span class="label">De:</span>
            <span class="value">{{ sourceAccount?.name }}</span>
          </div>
          <div class="detail-row">
            <span class="label">Vers:</span>
            <span class="value">{{ targetAccount?.name }}</span>
          </div>
          <div class="detail-row">
            <span class="label">Montant prélevé:</span>
            <span class="value">{{ transferForm.get('amount')?.value | currency:sourceAccount?.currency }}</span>
          </div>
          <div class="detail-row" *ngIf="sourceAccount && targetAccount && sourceAccount.currency !== targetAccount.currency">
            <span class="label">Montant crédité:</span>
            <span class="value">{{ getTargetAmount() | currency:targetAccount?.currency }}</span>
          </div>
          <div class="detail-row">
            <span class="label">Description:</span>
            <span class="value">{{ transferForm.get('description')?.value }}</span>
          </div>
        </div>

        <div class="warning-message" *ngIf="sourceAccount && transferForm.get('amount')?.value > sourceAccount.currentBalance">
          <div class="warning-icon">⚠️</div>
          <div class="warning-content">
            <strong>Attention:</strong> Le montant du transfert dépasse le solde actuel du compte source.
          </div>
        </div>

        <div class="confirmation-actions">
          <button type="button" class="btn btn-secondary" (click)="cancelTransfer()">Retour</button>
          <button type="button" class="btn btn-primary" (click)="confirmTransfer()">Confirmer le transfert</button>
        </div>
      </div>
    </div>
  </div>
</div>
