import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  title = 'TradeTracker';
  darkMode = false;

  ngOnInit(): void {
    // Check if dark mode preference exists in local storage
    const storedDarkMode = localStorage.getItem('darkMode');
    if (storedDarkMode) {
      this.darkMode = storedDarkMode === 'true';
    } else {
      // Check if user prefers dark mode at the OS level
      const prefersDarkMode = window.matchMedia && window.matchMedia('(prefers-color-scheme: dark)').matches;
      this.darkMode = prefersDarkMode;
    }
  }

  toggleDarkMode(): void {
    this.darkMode = !this.darkMode;
    localStorage.setItem('darkMode', this.darkMode.toString());
  }
}
<div class="app-container" [ngClass]="{'dark-mode': darkMode}">
  <header class="app-header">
    <div class="logo">
      <h1>{{ title }}</h1>
    </div>
    <nav class="main-nav">
      <ul>
        <li><a routerLink="/dashboard" routerLinkActive="active">Tableau de Bord</a></li>
        <li><a routerLink="/accounts" routerLinkActive="active">Comptes</a></li>
        <li><a routerLink="/transactions" routerLinkActive="active">Transactions</a></li>
        <li><a routerLink="/settings" routerLinkActive="active">Paramètres</a></li>
      </ul>
    </nav>
    <div class="theme-toggle">
      <button class="theme-toggle-btn" (click)="toggleDarkMode()" title="Changer de thème">
        <span *ngIf="!darkMode">🌙</span>
        <span *ngIf="darkMode">☀️</span>
      </button>
    </div>
  </header>

  <main class="app-content">
    <router-outlet></router-outlet>
  </main>

  <footer class="app-footer">
    <p>&copy; 2025 TradeTracker - Gérez intelligemment vos comptes de trading</p>
  </footer>
</div>
